import React, { useState, useEffect } from "react";
import { Form, Icon, Input, Button, notification, message } from "antd";

import { addStockApi, updateStockApi } from "../../../../api/stock";

import "./AddEditProductForm.scss";

export default function AddEditProductForm(props) {
  const { setIsVisibleModal, setReloadProducts, product } = props;
  const [productData, setProductData] = useState({});

  useEffect(() => {
    product ? setProductData(product) : setProductData({});
  }, [product]);

  const addStock= e => {
    e.preventDefault();

    if (!stockData.id) {
      notification["error"]({
        message: "El id del producto es obligatorio"
      });
    } else {
      addProductApi(prostockDataductData)
        .then(response => {
    const typeNotification = response.code === 200 ? "success" : "warning";
          notification[typeNotification]({
            message: response.message
          });
          setIsVisibleModal(false);
          setProductData({});
          setReloadProducts();
          setReloadProducts(true);


        })
        .catch(() => {
          notification["error"]({
            message: "Error del servidor, intentelo m치s tarde."
          });
        });
    }
  };

  const updateStock = e => {
    e.preventDefault();

    updateProductApi(product.id, productData)
      .then(response => {

        const typeNotification = response.code === 200 ? "success" : "warning";
        notification[typeNotification]({

          message: response.message
        });
        console.log(response);
        setIsVisibleModal(false);
        setProductData({});
        setReloadProducts(true);

      })
      .catch(() => {
        notification["error"]({
          message: "Error del servidor, intentelo m치s tarde."
        });
      });
  };

  return (
    <div className="add-edit-course-form">
      <AddEditForm
        stock={stock}
        addStock={addStock}
        updateStock={updateStock}
        stockData={stockData}
        setStockData={setStockData}
      />
    </div>
  );
}

function AddEditForm(props) {
  const { stock, addStock, updateStock, stockData, setStockData } = props;

  return (
    <Form
      className="form-add-edit"
      onSubmit={stock ? updateStock : addStock}
    >
      <Form.Item>
        <Input
          prefix={<Icon type="key" />}
          placeholder="Cantidad"
          value={stockData.quantity}
          onChange={e =>
            setStockData({ ...stockData, quantity: e.target.value })
          }
          disabled={stock ? true : false}
        />
      </Form.Item>
      <Form.Item>
        <Input
          prefix={<Icon type="key" />}
          placeholder="Cantidad disponible"
          value={stockData.quantityAvailable}
          onChange={e =>
            setStockData({ ...stockData, quantityAvailable: e.target.value })
          }
        />
      </Form.Item>
      <Form.Item>
        <Input
          prefix={<Icon type="key" />}
          placeholder="Variant"
          value={stockData.variant}
          onChange={e =>
            setStockData({ ...stockData, variant: e.target.value })
          }
        />
      </Form.Item>

      <Form.Item>
        <Button type="primary" htmlType="submit" className="btn-submit">
          {stock ? "Actualizar producto" : "Crear producto"}
        </Button>
      </Form.Item>
    </Form>
  );
}
